import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Contact.css'; // Make sure to create this CSS file

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    address: '',
    message: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    setFormData({ name: '', email: '', address: '', message: '' });
  };

  return (
    <div className="contact-page">
      <header>
        {/* Header content remains the same */}
      </header>

      <main role="main">
        <h1>Contact Us</h1>
        <div className="contact-container">
          <section id="contact-info">
            <h2>Get in Touch</h2>
            <p>We'd love to hear from you! Here's how you can reach us:</p>
            <ul>
              <li>Phone: (555) 123-4567</li>
              <li>Email: lcutler63@gmail.com</li>
              <li>Address: 1205 S. 4th St., Nebraska City, NE 68410</li>
            </ul>
          </section>

          <section id="contact-form">
            <h2>Send Us a Message</h2>
            <form onSubmit={handleSubmit} className="contact-form">
              <div className="form-group">
                <label htmlFor="name">Name:</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="address">Address:</label>
                <input
                  type="text"
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="message">Message:</label>
                <textarea
                  id="message"
                  name="message"
                  rows="5"
                  value={formData.message}
                  onChange={handleChange}
                  required
                ></textarea>
              </div>

              <button type="submit">Send Message</button>
            </form>
          </section>
        </div>

        <section id="map">
          {/* Map content remains the same */}
        </section>
      </main>

      <footer>
        <p>&copy; 2024 Your Cozy Retreats. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Contact;
